mapper.select:
SELECT
noticeid as boardid, noticetitle as title, noticeuserid as userid, noticetime as time
postid as boardid, posttitle as title, postuserid as userid, posttime as time
FROM ${boardtype} WHERE
noticeid = ${boardid}
postid = ${boardid}

mapper.update:
UPDATE ${boardtype} SET
noticetitle = #{title}, noticeuserid = #{userid}, noticetime = CURRENT_TIMESTAMP WHERE noticeid = ${boardid}
posttitle = #{title}, postuserid = #{userid}, posttime = CURRENT_TIMESTAMP WHERE postid = ${boardid}
