mapper.select:
SELECT
noticeid as boardid, noticetitle as title, noticeuserid as userid, noticetime as time, noticecontent as content
postid as boardid, posttitle as title, postuserid as userid, posttime as time, postcontent as content
FROM ${boardtype} WHERE
noticeid = ${boardid}
postid = ${boardid}

mapper.update:
UPDATE ${boardtype} SET


		noticetitle = #{title},
		noticeuserid = #{userid},
		noticetime = CURRENT_TIMESTAMP
		noticecontent = #{content}
		WHERE noticeid = ${boardid}


		posttitle = #{title},
		postuserid = #{userid},
		posttime = CURRENT_TIMESTAMP
		postcontent = #{content}
		WHERE postid = ${boardid}
